import React, { useState } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setName("");
    setEmail("");
    setPassword("");
  };

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (name == "") setNameError(true);

    if (email == "") setEmailError(true);

    if (password == "") setPasswordError(true);

    if (name.length && email.length && password.length) {
      const User = {
        name: name,
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/user/register", User)
        .then((response) => {
          alert(response.data.name + " , you have been registered successfully !");
          console.log(response.data);
        })
        .catch((err) => {
            alert("Account Already exists ! Please Login.");
            <Link to="/login"></Link>
            console.log(err); 
          });

      resetInputs();
    }
  };

  return (
    <div className="Login">
      <br></br>
      <br></br>

      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        style={{ minHeight: "100vh" }}
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <br></br>
          <br></br>
          <form noValidate autoComplete="off">
            <TextField
              value={name}
              onChange={(e) => setName(e.target.value)}
              label="Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={nameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Sign Up
          </Button>
          <br></br>
          <br></br>
          <Grid item xs={10} style={{ border: "2px solid black" }}>
          <br></br>
          &emsp; Already have an account ? &emsp;
          <Link to="/login">
            <Button
              type="submit"
              variant="contained"
              size="small"
              color="primary"
            >
              Login
            </Button>
          </Link>
          &emsp;
          <br></br>
          <br></br>
        </Grid>
        <br></br>
        <br></br>
      </Grid>
      </Grid>
    </div>
  );
}
