import React, { useState } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";
import Box from "@mui/material/Box";
import Fab from "@mui/material/Fab";
import Stack from "@mui/material/Stack";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

function RegisterVendors() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const [age, setAge] = useState("");
  const [batch, setBatch] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setName("");
    setEmail("");
    setPassword("");
  };

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [contactError, setContactError] = useState(false);
  const [ageError, setAgeError] = useState(false);
  const [batchError, setBatchError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (name == "") setNameError(true);
    if (email == "") setEmailError(true);
    if (contact == "") setContactError(true);
    if (age == "") setAgeError(true);
    if (batch == "") setBatchError(true);
    if (password == "") setPasswordError(true);
    else {
      const User = {
        name: name,
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/user/register", User)
        .then((response) => {
          alert(
            response.data.name + " , you have been registered successfully !"
          );
          console.log(response.data);
        })
        .catch((err) => {
          alert("Account Already exists ! Please Login.");
          <Link to="/login"></Link>;
          console.log(err);
        });

      resetInputs();
    }
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={name}
              size="small"
              onChange={(e) => setName(e.target.value)}
              label="Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={nameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={email}
              size="small"
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={contact}
              size="small"
              onChange={(e) => setContact(e.target.value)}
              label="Contact"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={contactError}
            />
            <br></br>
            <br></br>
            <TextField
              value={age}
              size="small"
              onChange={(e) => setAge(e.target.value)}
              label="Age"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={ageError}
            />
            <br></br>
            <br></br>
            <Box
              sx={{
                "& > :not(style)": { m: 1 },
                alignItems: "center",
                justifyContent: "center",
                borderRadius: "12px",
              }}
            >
              <TextField
                value={batch}
                disabled
                size="small"
                onChange={(e) => setBatch(e.target.value)}
                label="Select Batch"
                variant="outlined"
                color="primary"
                required
                error={batchError}
              />
              <br></br>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG1");
                }}
              >
                UG1
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG2");
                }}
              >
                UG2
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG3");
                }}
              >
                UG3
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG4");
                }}
              >
                UG4
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG5");
                }}
              >
                UG5
              </Button>
            </Box>
            <br></br>
            <TextField
              value={password}
              size="small"
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Sign Up
          </Button>
          <br></br>
          <br></br>
        </Grid>
      </Grid>
    </div>
  );
}

