import React, { Component, useState } from "react";
import { Navigate, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LoginIcon from "@mui/icons-material/Login";
import GoogleIcon from "@mui/icons-material/Google";
import GoogleLogin from "react-google-login";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

function VendorLogin() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setEmail("");
    setPassword("");
  };

  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  function ShowVendorLogin() {
    return (
      <Link
        to={{
          pathname: "/vendor/" + _id,
        }}
      >
        <Button>
          <Tooltip TransitionComponent={Zoom} title="Log In" arrow>
            <Button
              type="submit"
              color="success"
              sx={{ alignItems: "center" }}
              variant="contained"
            >
              <LoginIcon></LoginIcon>
            </Button>
          </Tooltip>
        </Button>
      </Link>
    );
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    if (email == "") setEmailError(true);

    if (password == "") setPasswordError(true);

    if (email.length && password.length) {
      const Vendor = {
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/vendor/login", Vendor)
        .then((response) => {
          alert(response.data.managerName + ", your credentials have been verified !");
        })
        .catch((err) => {
          alert("Invalid user name or password !");
          console.log(err); //Logs a string: Error: Request failed with status code 404
        });

      resetInputs();
    } else console.log("Invalid email or password !");
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        borderRadius={16}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Login
          </Button>
        </Grid>
        <br></br>
      </Grid>
    </div>
  );
}

function BuyerLogin() {
  const [email, setEmail] = useState("");
  const [_id, set_id] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setEmail("");
    setPassword("");
  };

  function ShowBuyerLogin() {
    return (
      <Link
        to={{
          pathname: "/buyer/" + _id,
        }}
      >
        <Button>
          <Tooltip TransitionComponent={Zoom} title="Log In" arrow>
            <Button
              type="submit"
              color="success"
              sx={{ alignItems: "center" }}
              variant="contained"
            >
              <LoginIcon></LoginIcon>
            </Button>
          </Tooltip>
        </Button>
      </Link>
    );
  }

  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);
  const [hideLogin, setHideLogin] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (email == "") setEmailError(true);

    if (password == "") setPasswordError(true);

    if (email.length && password.length) {
      const Buyer = {
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/buyer/login", Buyer)
        .then((response) => {
          alert(response.data.name + ", Your credentials have been verified !");
          setHideLogin(true);

          const Buyer_Email = {
            email: email,
          };

          axios
            .post("http://localhost:5000/buyer/_id", Buyer_Email)
            .then((response) => {
              set_id(response.data);
            });
            resetInputs();
        })
        .catch(function (err) {
          alert("Invalid user name or password !");
          console.log(err);
        });
    } else console.log("Invalid email or password !");
    // resetInputs();
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        borderRadius={16}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Login
          </Button>
          {hideLogin ? <ShowBuyerLogin></ShowBuyerLogin> : null}
          <br></br>
          <br></br>
          <Grid
            item
            xs={10}
            style={{ border: "2px solid blue", borderRadius: 8 }}
          >
            <br></br>
            &emsp; Sign In through Google &emsp;
            <Link to="/register">
              <Button
                type="submit"
                variant="contained"
                color="primary"
                sx={{ borderRadius: 8 }}
              >
                <GoogleIcon></GoogleIcon>
              </Button>
            </Link>
            &emsp;
            <br></br>
            <br></br>
          </Grid>
        </Grid>
      </Grid>
    </div>
  );
}

