import React, { useState } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";
import Box from "@mui/material/Box";
import Fab from "@mui/material/Fab";
import Stack from "@mui/material/Stack";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

function RegisterVendors() {
  const [managerName, setManagerName] = useState("");
  const [shopName, setShopName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setManagerName("");
    setEmail("");
    setPassword("");
  };

  const [managerNameError, setManagerNameError] = useState(false);
  const [shopNameError, setShopNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [contactError, setContactError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (managerName == "") setManagerNameError(true);
    if (shopName == "") setShopNameError(true);
    if (email == "") setEmailError(true);
    if (contact == "") setContactError(true);
    if (password == "") setPasswordError(true);
    else {
      const User = {
        name: managerName,
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/user/register", User)
        .then((response) => {
          alert(
            response.data.name + " , you have been registered successfully !"
          );
          console.log(response.data);
        })
        .catch((err) => {
          alert("Account Already exists ! Please Login.");
          <Link to="/login"></Link>;
          console.log(err);
        });

      resetInputs();
    }
  };

