              variant="outlined"
              color="primary"
              required
              error={nameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={email}
              size="small"
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
