import React, { useState } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";
import Box from "@mui/material/Box";
import Fab from "@mui/material/Fab";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

function RegisterVendors() {
  const [managerName, setManagerName] = useState("");
  const [shopName, setShopName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const [password, setPassword] = useState("");

  // opening time of the shop
  const [openHH, setOpenHH] = useState("");
  const [openMM, setOpenMM] = useState("");

  // close time of the shop
  const [closeHH, setCloseHH] = useState("");
  const [closeMM, setCloseMM] = useState("");

  const resetInputs = () => {
    setManagerName("");
    setEmail("");
    setPassword("");
  };

  const [managerNameError, setManagerNameError] = useState(false);
  const [shopNameError, setShopNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [contactError, setContactError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const [openHHError, setOpenHHError] = useState(false);
  const [openMMError, setOpenMMError] = useState(false);

  const [closeHHError, setCloseHHError] = useState(false);
  const [closeMMError, setCloseMMError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (managerName == "") setManagerNameError(true);
    if (shopName == "") setShopNameError(true);
    if (email == "") setEmailError(true);
    if (contact == "") setContactError(true);
    if (openHH == "" || openHH < 0 || openHH > 23) setOpenHHError(true);
    if (openMM == "" || openMM < 0 || openMM > 59) setOpenMMError(true);
    if (closeHH == "" || closeHH < 0 || closeHH > 23) setCloseHHError(true);
    if (closeMM == "" || closeMM < 0 || closeMM > 59) setCloseMMError(true);
    if (password == "") setPasswordError(true);
    else {
      const User = {
        name: managerName,
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/user/register", User)
        .then((response) => {
          alert(
            response.data.name + " , you have been registered successfully !"
          );
          console.log(response.data);
        })
        .catch((err) => {
          alert("Account Already exists ! Please Login.");
          <Link to="/login"></Link>;
          console.log(err);
        });

      resetInputs();
    }
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={managerName}
              size="small"
              onChange={(e) => setManagerName(e.target.value)}
              label="Manager Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={managerNameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={shopName}
              size="small"
              onChange={(e) => setShopName(e.target.value)}
              label="Shop Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={shopNameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={email}
              size="small"
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={contact}
              size="small"
              onChange={(e) => setContact(e.target.value)}
              label="Contact"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={contactError}
            />
            <br></br>
            <br></br>
            <Grid
              container
              align="center"
              justify="center"
              alignItems="center"
              spacing={0}
              direction="column"
              alignItems="center"
              className={useStyles.field}
              sx={{ borderRadius: 16 }}
            >
              <TextField
                  value={openHH}
                  size="small"
                  onChange={(e) => {setOpenHH(e.target.value) + " : " + setOpenMM(e.target.value)}}
                  label="Opening Time"
                  type="text"
                  variant="outlined"
                  color="primary"
                  required
                />
              <Grid
                item
                xs={8}
                style={({ border: "2px solid" }, { minWidth: "100vh" })}
              >
                <TextField
                  value={openHH}
                  size="small"
                  onChange={(e) => setOpenHH(e.target.value)}
                  InputProps={{ inputProps: { min: 0, max: 24 } }}
                  label="H (0-23)"
                  type="Number"
                  variant="outlined"
                  color="primary"
                  required
                  error={openHHError}
                />
                &emsp;
                <TextField
                  value={openMM}
                  size="small"
                  onChange={(e) => setOpenMM(e.target.value)}
                  InputProps={{ inputProps: { min: 0, max: 59 } }}
                  label="M (0-59)"
                  type="Number"
                  variant="outlined"
                  color="primary"
                  required
                  error={openMMError}
                />
              </Grid>
            </Grid>

            <br></br>
            <br></br>
            <TextField
              value={password}
              size="small"
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Sign Up
          </Button>
          <br></br>
        </Grid>
      </Grid>
    </div>
  );
}

function RegisterBuyers() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");

  const [age, setAge] = useState("");
  const minAge = 16;
  const maxAge = 60;

  const [batch, setBatch] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setName("");
    setEmail("");
    setContact("");
    setAge("");
    setBatch("");
    setPassword("");
  };

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [contactError, setContactError] = useState(false);
  const [ageError, setAgeError] = useState(false);
  const [batchError, setBatchError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (name == "") setNameError(true);
    if (email == "") setEmailError(true);
    if (contact == "") setContactError(true);
    if (age == "" || age < minAge || age > maxAge) setAgeError(true);
    if (batch == "") setBatchError(true);
    if (password == "") setPasswordError(true);
    else {
      const Buyer = {
        name: name,
        email: email,
        contact: contact,
        age: age,
        batch: batch,
        password: password,
      };

      axios
        .post("http://localhost:5000/buyer/register", Buyer)
        .then((response) => {
          alert(
            response.data.name + " , you have been registered successfully !"
          );
          console.log(response.data);
        })
        .catch((err) => {
          alert("Account Already exists ! Please Login.");
          console.log(err);
        });

      resetInputs();
    }
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={name}
              size="small"
              onChange={(e) => setName(e.target.value)}
              label="Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={nameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={email}
              size="small"
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={contact}
              size="small"
              onChange={(e) => setContact(e.target.value)}
              label="Contact"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={contactError}
            />
            <br></br>
            <br></br>
            <TextField
              value={age}
              size="small"
              onChange={(e) => setAge(e.target.value)}
              InputProps={{ inputProps: { min: 16, max: 60 } }}
              label="Age"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={ageError}
            />
            <br></br>
            <br></br>
            <Box
              sx={{
                "& > :not(style)": { m: 1 },
                alignItems: "center",
                justifyContent: "center",
                borderRadius: "12px",
              }}
            >
              <TextField
                value={batch}
                disabled
                size="small"
                onChange={(e) => setBatch(e.target.value)}
                label="Select Batch"
                variant="outlined"
                color="primary"
                required
                error={batchError}
              />
              <br></br>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG1");
                }}
              >
                UG1
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG2");
                }}
              >
                UG2
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG3");
                }}
              >
                UG3
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG4");
                }}
              >
                UG4
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG5");
                }}
              >
                UG5
              </Button>
            </Box>
            <br></br>
            <TextField
              value={password}
              size="small"
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Sign Up
          </Button>
        </Grid>
      </Grid>
    </div>
  );
}

export default function Register() {
  const [regBuy, setShowRegBuy] = useState(false);
  const [regVen, setShowRegVen] = useState(false);

  return (
    <Box
      sx={{
        justifyContent: "center",
        justifyItems: "center",
      }}
    >
      <Box
        sx={{
          "& > :not(style)": { m: 1 },
          display: "flex",
          justifyContent: "center",
          justifyItems: "center",
        }}
      >
        <Fab
          type="submit"
          color="primary"
          variant="extended"
          onClick={function () {
            if (regBuy == false) {
              if (regVen == true) setShowRegVen(false);
              setShowRegBuy(true);
            } else setShowRegBuy(false);
          }}
        >
          BUYERS
        </Fab>
        <Fab
          color="primary"
          variant="extended"
          onClick={function () {
            if (regVen == false) {
              if (regBuy == true) setShowRegBuy(false);
              setShowRegVen(true);
            } else setShowRegVen(false);
          }}
        >
          VENDOR
        </Fab>
      </Box>
      {regBuy ? <RegisterBuyers></RegisterBuyers> : null}
      {regVen ? <RegisterVendors></RegisterVendors> : null}
      <br></br>
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={10}
          style={{
            border: "2px solid",
            borderColor: "primary.main",
            borderRadius: "5px",
          }}
        >
          <br></br>
          &emsp; Already have an account ? &emsp;
          <Link to="/login">
            <Button
              type="submit"
              variant="contained"
              size="small"
              color="primary"
            >
              Login
            </Button>
          </Link>
          &emsp;
          <br></br>
          <br></br>
        </Grid>
      </Grid>
      <br></br>
      <br></br>
    </Box>
  );
}
