import React, { Component, useState } from "react";
import { Navigate, useNavigate } from "react-router-dom";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";
import Box from "@mui/material/Box";
import GoogleIcon from "@mui/icons-material/Google";
import GoogleLogin from "react-google-login";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

const handleFailure = (result) => {
  alert(result);
};

const handleLogin = (googleData) => {
  console.log(googleData);
};

function VendorLogin() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setEmail("");
    setPassword("");
  };

  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (email == "") setEmailError(true);

    if (password == "") setPasswordError(true);

    if (email.length && password.length) {
      const Vendor = {
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/vendor/login", Vendor)
        .then((response) => {
          alert(response.data.name + " , you have logged in");
          console.log(response.data);
        })
        .catch((err) => {
          alert("Invalid user name or password !");
          console.log(err); //Logs a string: Error: Request failed with status code 404
        });

      resetInputs();
    } else console.log("Invalid email or password !");
  };

  return (
    <div className="Login">
      <br></br>
      <br></br>

      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        style={{ minHeight: "100vh" }}
        borderRadius={16}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <br></br>
          <br></br>
          <form noValidate autoComplete="off">
            <TextField
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Login
          </Button>
          <br></br>
          <br></br>
          <Grid item xs={10} style={{ border: "2px solid black" }}>
            <br></br>
            &emsp; Don't have an account ? &emsp;
            <Link to="/register">
              <Button
                type="submit"
                variant="contained"
                size="small"
                color="primary"
              >
                Sign Up
              </Button>
            </Link>
            &emsp;
            <br></br>
            <br></br>
          </Grid>
          <br></br>
          <br></br>
        </Grid>
        <br></br>
        <br></br>
      </Grid>
    </div>
  );
}

function BuyerLogin() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setEmail("");
    setPassword("");
  };

  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (email == "") {
      setEmailError(true);
      return 0;
    }

    if (password == "") {
      setPasswordError(true);
      return 0;
    }

    if (email.length && password.length) {
      const Buyer = {
        email: email,
        password: password,
      };

      axios
        .post("http://localhost:5000/buyer/login", Buyer)
        .then((response) => {
          console.log(response.data);
          return 1;
        })
        .catch(function (err) {
          alert("Invalid user name or password !");
          console.log(err);
          return 0;
        });

      resetInputs();
    } else {
      console.log("Invalid email or password !");
      return 0;
    }
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        borderRadius={16}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <br></br>
          <Button
            onClick={function () {
              if (handleSubmit) {
                <Navigate to="/"></Navigate>;
                alert("You have logged in successfully !");
              } else {
                alert("Invalid email or password !");
              }
            }}
            type="submit"
            variant="contained"
            color="success"
          >
            Login
          </Button>
          <br></br>
          <br></br>
          <Grid
            item
            xs={10}
            style={{ border: "2px solid blue", borderRadius: 8 }}
          >
            <br></br>
            &emsp; Sign In through Google &emsp;
            <Link to="/register">
              <Button
                type="submit"
                variant="contained"
                color="primary"
                sx={{ borderRadius: 8 }}
              >
                <GoogleIcon></GoogleIcon>
              </Button>
            </Link>
            &emsp;
            <br></br>
            <br></br>
          </Grid>
        </Grid>
      </Grid>
    </div>
  );
}

