import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation, useParams } from "react-router-dom";
import Badge from "@mui/material/Badge";
import HomeIcon from "@mui/icons-material/Home";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import { Typography } from "@mui/material";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LogoutIcon from "@mui/icons-material/Logout";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import AddCardIcon from "@mui/icons-material/AddCard";
import TextField from "@mui/material/TextField";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";

import SearchIcon from "@mui/icons-material/Search";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "./Nav.css";

export default function Buyer_Profile() {
  const _id = "" + useParams().username;
  const [email, setEmail] = useState("");
  const [orders, setOrders] = useState();
  const [viewOrders, setViewOrders] = useState(false);

  useEffect(() => {
    axios
      .post("http://localhost:5000/buyer/email", { _id })
      .then((response) => {
        setEmail(response.data);
        console.log(response.data);
      })
      .catch((error) => {});

    axios
      .post("http://localhost:5000/order/buyer-orders", { buyerEmail: email })
      .then((response) => {
        setOrders(response.data);
        console.log(response.data);
      })
      .catch((error) => {});
  }, []);

  function ShowOrders() {
    return (
      <Box sx={{ alignItems: "center" }}>
        <br></br>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Shop</TableCell>
              <TableCell>Item Name</TableCell>
              <TableCell>Price</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map((order, ind) => (
              <TableRow key={ind}>
                <TableCell>{order.shopName}</TableCell>
                <TableCell>{order.itemName}</TableCell>
                <TableCell>{order.price}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Box>
    );
  }

  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Welcome to Profile Page</h3>
            </Typography>
          </Box>
          &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Link
              to={{
                pathname: "/buyer/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Home" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <HomeIcon fontSize="large"></HomeIcon>
                </Button>
              </Tooltip>
            </Link>
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button sx={{ alignItems: "center", borderRadius: "16px" }}>
                  <LogoutIcon fontSize="large"></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
        </div>
      </header>
      <Box>
        <Grid
          container
          justify="center"
          align="center"
          direction="row"
          justifyContent={"center"}
          spacing={4}
        >
          <Grid item xs={6}>
            <br></br>
            <Button
              variant="outlined"
              onClick={function () {
                if (viewOrders == false) {
                  setViewOrders(true);
                  axios
                    .post("http://localhost:5000/order/buyer-orders", {
                      buyerEmail: email,
                    })
                    .then((response) => {
                      setOrders(response.data);
                      console.log(response.data);
                    })
                    .catch((error) => {});
                } else setViewOrders(false);
              }}
            >
              View your Orders
            </Button>
            {viewOrders ? <ShowOrders></ShowOrders> : null}
          </Grid>
        </Grid>
      </Box>
      <br></br>
    </div>
  );
}
