import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation, useParams } from "react-router-dom";
import Badge from "@mui/material/Badge";
import HomeIcon from "@mui/icons-material/Home";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import { Typography } from "@mui/material";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LogoutIcon from "@mui/icons-material/Logout";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import AddCardIcon from "@mui/icons-material/AddCard";
import TextField from "@mui/material/TextField";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";

import SearchIcon from "@mui/icons-material/Search";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "./Nav.css";

export default function Buyer_Profile() {
  const _id = "" + useParams().username;
  const [profile, setProfile] = useState();
  const [viewProfile, setViewProfile] = useState(false);
  const [editMenu, setEditMenu] = useState(false);

  useEffect(() => {
    axios
      .post("http://localhost:5000/buyer/user-profile", { _id })
      .then((response) => {
        setProfile(response.data);
        console.log(response.data);
      })
      .catch((error) => {});
  }, []);

  function ShowProfile(props) {
    return (
      <Box sx={{ alignItems: "center" }}>
        <br></br>
        <Table size="small">
          <TableHead></TableHead>
          <TableBody>
            <TableRow>
              <TableCell>Name : </TableCell>
              <TableCell>{props.children.name}</TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Email ID : </TableCell>
              <TableCell>{props.children.email}</TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Contact Info : </TableCell>
              <TableCell>{props.children.contact}</TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Age : </TableCell>
              <TableCell>{props.children.age}</TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Batch : </TableCell>
              <TableCell>{props.children.batch}</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </Box>
    );
  }

  function EditProfile() {
    const [name, setName] = useState("");
    const [nameError, setNameError] = useState(false);

    const [contact, setContact] = useState();
    const [contactError, setContactError] = useState(false);

    const [age, setAge] = useState();
    const [ageError, setAgeError] = useState(false);
    const minAge = 16;
    const maxAge = 60;

    const [batch, setBatch] = useState("");
    const [batchError, setBatchError] = useState(false);

    function ResetInputs() {
      setName("");
      setContact("");
      setAge("");
      setBatch("");
    }
    return (
      <div>
        <br></br>
        <form noValidate autoComplete="off">
          <Paper sx={{ alignItems: "center" }}>
            <Typography>Edit Menu</Typography>
            <br></br>
            <TextField
              value={name}
              size="small"
              onChange={(e) => setName(e.target.value)}
              label="Change Name"
              type="text"
              variant="outlined"
              color="primary"
              error={nameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={contact}
              size="small"
              onChange={(e) => setContact(e.target.value)}
              label="Contact"
              type="Number"
              variant="outlined"
              color="primary"
              error={contactError}
            />
            <br></br>
            <br></br>
            <TextField
              value={age}
              size="small"
              onChange={(e) => setAge(e.target.value)}
              InputProps={{ inputProps: { min: 16, max: 60 } }}
              label="Age"
              type="Number"
              variant="outlined"
              color="primary"
              error={ageError}
            />
            <br></br>
            <br></br>
            <Box
              sx={{
                "& > :not(style)": { m: 1 },
                alignItems: "center",
                justifyContent: "center",
                borderRadius: "12px",
              }}
            >
              <TextField
                value={batch}
                disabled
                size="small"
                onChange={(e) => setBatch(e.target.value)}
                label="Select Batch"
                variant="outlined"
                color="primary"
                error={batchError}
              />
              <br></br>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG1");
                }}
              >
                UG1
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG2");
                }}
              >
                UG2
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG3");
                }}
              >
                UG3
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG4");
                }}
              >
                UG4
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG5");
                }}
              >
                UG5
              </Button>
              <br></br>
              <Button
                variant="contained"
                color="success"
                onClick={function () {
                  var check = 0;

                  if (name != "") {
                    axios
                      .post(
                        "http://localhost:5000/buyer/user-profile/edit-name",
                        { _id, name }
                      )
                      .then((response) => {})
                      .catch((error) => {});
                    check = 1;
                  }

                  if (batch != "") {
                    axios
                      .post(
                        "http://localhost:5000/buyer/user-profile/edit-batch",
                        { _id, batch }
                      )
                      .then((response) => {})
                      .catch((error) => {});
                    check = 1;
                  }

                  if (!isNaN(age) && age > minAge && age < maxAge) {
                    axios
                      .post(
                        "http://localhost:5000/buyer/user-profile/edit-age",
                        { _id, age }
                      )
                      .then((response) => {})
                      .catch((error) => {});
                    check = 1;
                  }

                  if (!isNaN(contact)) {
                    axios
                      .post(
                        "http://localhost:5000/buyer/user-profile/edit-contact",
                        { _id, contact }
                      )
                      .then((response) => {})
                      .catch((error) => {});
                    check = 1;
                  }

                  if (check == 0) {
                    setNameError(true);
                    setAgeError(true);
                    setBatchError(true);
                    setContactError(true);
                  } else {
                    ResetInputs();

                    axios
                      .post("http://localhost:5000/buyer/user-profile", { _id })
                      .then((response) => {
                        setProfile(response.data);
                      })
                      .catch((error) => {});

                    if (viewProfile == true) {
                      setViewProfile(false);
                      setTimeout(function () {
                        setViewProfile(true);
                      }, 3000);
                    }

                    alert("Fields have been updated successfully !");
                  }
                }}
              >
                Edit
              </Button>
              <br></br>
            </Box>
          </Paper>
          <br></br>
        </form>
      </div>
    );
  }

  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Welcome to Profile Page</h3>
            </Typography>
          </Box>
          &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Link
              to={{
                pathname: "/buyer/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Home" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <HomeIcon fontSize="large"></HomeIcon>
                </Button>
              </Tooltip>
            </Link>
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button sx={{ alignItems: "center", borderRadius: "16px" }}>
                  <LogoutIcon fontSize="large"></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
        </div>
      </header>
      <Box>
        <Grid
          container
          justify="center"
          align="center"
          spacing={0}
          direction="row"
          justifyContent={"center"}
          spacing={4}
        >
          <Grid item xs={3}>
            <br></br>
            <Button
              variant="outlined"
              onClick={function () {
                if (viewProfile == false) {
                  axios
                    .post("http://localhost:5000/buyer/user-profile", { _id })
                    .then((response) => {
                      setProfile(response.data);
                    })
                    .catch((error) => {});
                  setViewProfile(true);
                  console.log(profile);
                } else setViewProfile(false);
              }}
            >
              View Profile
            </Button>
            {viewProfile ? <ShowProfile>{profile}</ShowProfile> : null}
          </Grid>
          <Grid item xs={4}>
            <br></br>
            <Button
              variant="outlined"
              onClick={function () {
                if (editMenu == false) {
                  setEditMenu(true);
                } else setEditMenu(false);
              }}
            >
              Edit Profile
            </Button>
            <br></br>
            {editMenu ? <EditProfile></EditProfile> : null}
          </Grid>
        </Grid>
      </Box>
      <br></br>
    </div>
  );
}

