import React, { useState } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";
import Box from "@mui/material/Box";
import Fab from "@mui/material/Fab";
import CachedIcon from "@mui/icons-material/Cached";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

function RegisterBuyers() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");

  const [college, setCollege] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setName("");
    setEmail("");
    setContact("");
    setAge("");
    setCollege("");
    setPassword("");
  };

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [contactError, setContactError] = useState(false);
  const [ageError, setAgeError] = useState(false);
  const [batchError, setBatchError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (name == "") setNameError(true);
    if (email == "") setEmailError(true);
    if (contact == "") setContactError(true);
    if (age == "" || age < minAge || age > maxAge) setAgeError(true);
    if (batch == "") setBatchError(true);
    if (password == "") setPasswordError(true);
    else {
      const Buyer = {
        name: name,
        email: email,
        contact: contact,
        age: age,
        batch: batch,
        wallet: 0,
        orders: [],
        password: password,
      };

      axios
        .post("http://localhost:5000/buyer/register", Buyer)
        .then((response) => {
          alert(
            response.data.name + " , you have been registered successfully !"
          );
          console.log(response.data);
        })
        .catch((err) => {
          alert("Account Already exists ! Please Login.");
          console.log(err);
        });

      resetInputs();
    }
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={name}
              size="small"
              onChange={(e) => setName(e.target.value)}
              label="Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={nameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={email}
              size="small"
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={contact}
              size="small"
              onChange={(e) => setContact(e.target.value)}
              label="Contact"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={contactError}
            />
            <br></br>
            <br></br>
            <TextField
              value={age}
              size="small"
              onChange={(e) => setAge(e.target.value)}
              InputProps={{ inputProps: { min: 16, max: 60 } }}
              label="Age"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={ageError}
            />
            <br></br>
            <br></br>
            <Box
              sx={{
                "& > :not(style)": { m: 1 },
                alignItems: "center",
                justifyContent: "center",
                borderRadius: "12px",
              }}
            >
              <TextField
                value={batch}
                disabled
                size="small"
                onChange={(e) => setBatch(e.target.value)}
                label="Select Batch"
                variant="outlined"
                color="primary"
                required
                error={batchError}
              />
              <br></br>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG1");
                }}
              >
                UG1
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG2");
                }}
              >
                UG2
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG3");
                }}
              >
                UG3
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG4");
                }}
              >
                UG4
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setBatch("UG5");
                }}
              >
                UG5
              </Button>
            </Box>
            <br></br>
            <TextField
              value={password}
              size="small"
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Sign Up
          </Button>
        </Grid>
      </Grid>
    </div>
  );
}

export default function Register() {
  const [regBuy, setShowRegBuy] = useState(false);
  const [regVen, setShowRegVen] = useState(false);

  return (
    <Box
      sx={{
        justifyContent: "center",
        justifyItems: "center",
      }}
    >
      <Box
        sx={{
          "& > :not(style)": { m: 1 },
          display: "flex",
          justifyContent: "center",
          justifyItems: "center",
        }}
      >
        <Fab
          type="submit"
          color="primary"
          variant="extended"
          onClick={function () {
            if (regBuy == false) {
              if (regVen == true) setShowRegVen(false);
              setShowRegBuy(true);
            } else setShowRegBuy(false);
          }}
        >
          BUYERS
        </Fab>
        <Fab
          color="primary"
          variant="extended"
          onClick={function () {
            if (regVen == false) {
              if (regBuy == true) setShowRegBuy(false);
              setShowRegVen(true);
            } else setShowRegVen(false);
          }}
        >
          VENDOR
        </Fab>
      </Box>
      {regBuy ? <RegisterBuyers></RegisterBuyers> : null}
      {regVen ? <RegisterVendors></RegisterVendors> : null}
      <br></br>
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={10}
          style={{
            border: "2px solid",
            borderColor: "primary.main",
            borderRadius: "5px",
          }}
        >
          <br></br>
          &emsp; Already have an account ? &emsp;
          <Link to="/login" style= {{ textDecoration: "inherit"}}>
            <Button
              type="submit"
              variant="contained"
              size="small"
              color="primary"
            >
              Login
            </Button>
          </Link>
          &emsp;
          <br></br>
          <br></br>
        </Grid>
      </Grid>
      <br></br>
      <br></br>
    </Box>
  );
}
