import React from "react";
import { Link } from "react-router-dom";

import "./Nav.css";

import Button from "@mui/material/Button";
import LoginIcon from "@mui/icons-material/Login";
import { Typography } from "@mui/material";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";

const useVisibilityToggler = (component, visibility = false) => {
  const [visible, setVisibility] = useState() (() => visibility);

  return [
    visible ? component : null,
    () => setVisibility( v => !v)
  ]
}

function Nav() {

  const [Component, toggleVisibility] = useVisibilityToggler(<LoginIcon>Login</LoginIcon>, true)

  return (
    <header>
      <div className="Navigator">
        <nav>
          <li>
            <Typography style={{ flex: 1 }}>
              <h1>&emsp;&emsp;CANTEEN PORTAL</h1>
            </Typography>
          </li>
          <Link to="/login">
            <li>
              <Tooltip TransitionComponent={Zoom} title="Log In" arrow>
                <Button 
                variant="contained" 
                style={{ height: 40 }}
                onClick={toggleVisibility}
                >
                  { Component }
                </Button>
              </Tooltip>
            </li>
          </Link>
        </nav>
      </div>
    </header>
  );
}

export default Nav;
