function BasicMenu() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <Button
        id="basic-button"
        aria-controls={open ? 'basic-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
        onClick={handleClick}
      >
        Dashboard
      </Button>
      <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'basic-button',
        }}
      >
        <MenuItem onClick={handleClose}>Profile</MenuItem>
        <MenuItem onClick={handleClose}>My account</MenuItem>
        <MenuItem onClick={handleClose}>Logout</MenuItem>
      </Menu>
    </div>
  );
}

function UsersList() {
  const length = 5;
  const [vendors, setVendors] = useState([]);
  const [array, setArray] = useState(new Array(length).fill(0));

  useEffect(() => {
    axios
      .get("http://localhost:5000/food/vendor-food")
      .then((response) => {
        setVendors(response.data);
      })
      .catch((error) => {});
  }, []);

  function ShowVendorFood(props) {
    return (
      <Paper>
        <Table size="small">
          <TableHead align="center">
            <TableRow>
              <TableCell>Item Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {props.children.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell align="center">{food.itemName}</TableCell>
                <TableCell align="center">{food.price}</TableCell>
                <TableCell align="center">{food.category}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }

  return (
    <div>
      <Grid
        container
        alignItems="flex-start"
        spacing={0}
        direction="column"
        marginLeft={"40px"}
        sx={{ borderRadius: 16 }}
      >
        <br></br>
        <Grid item xs={6}>
          <Paper>
            <Table size="small">
              <TableHead>
                <TableRow>
                  <TableCell>Vendor Name</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {vendors.map((vendor, ind) => (
                  <TableRow key={ind}>
                    <TableCell>
                      {vendor._id}
                      <Button
                        size="small"
                        onClick={function () {
                          const list = [];
                          for (var i = 0; i < length; i++) {
                            if (i == ind && array[ind] == 0) list.push(1);
                            else if (i == ind && array[ind] == 1) list.push(0);
                            else list.push(array[i]);
                          }
                          setArray(list);
                        }}
                      >
                        <FastfoodIcon></FastfoodIcon>
                      </Button>
                      {array[ind] ? (
                        <ShowVendorFood>{vendor.details}</ShowVendorFood>
                      ) : null}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
}

export default function BuyerPage() {
  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Food Items</h3>
            </Typography>
          </Box>
          <BasicMenu></BasicMenu>
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                  variant="contained"
                >
                  <LogoutIcon></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
          &emsp;
        </div>
      </header>
      <UsersList></UsersList>
    </div>
  );
}
