import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { toUnitless } from "@mui/material/styles/cssUtils";
import { makeStyles } from "@material-ui/core";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  function validateForm() {
    return email.length > 0 && password.length > 0;
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    if (email == "") setEmailError(true);

    if (password == "") setPasswordError(true);

    if (email.length && password.length) console.log(email, password);
    else console.log("Invalid email or password !");
  };

  return (
    <div className="Login">
      <br></br>
      <br></br>

      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        style={{ minHeight: "100vh" }, {'borderRadius':8}}
      >
        <Grid item xs={8} style={{ border: "2px solid" }}>
          <br></br>
          <br></br>
          <form noValidate autoComplete="off">
            <TextField
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              onChange={(e) => setPassword(e.target.value)}
              label="Password"
              type="password"
              variant="outlined"
              color="primary"
              htmlFor="standard-adornment-password"
              required
              error={passwordError}
            />
          </form>
          <br></br>
          <br></br>
          <Button
            onClick={handleSubmit}
            type="submit"
            variant="contained"
            color="success"
          >
            Login
          </Button>
          <br></br>
          <br></br>
        </Grid>
        <br></br>
        <br></br>
        <Grid
          item
          xs={10}
          style={{ border: "2px solid" }}
        >
          <br></br>
          &emsp; Don't have an account ? &emsp;
          <Button
            type="submit"
            variant="contained"
            size="small"
            color="primary"
          >
            Sign Up
          </Button>
          &emsp;
          <br></br>
          <br></br>
        </Grid>
      </Grid>
    </div>
  );
}
