import { useState, useEffect } from "react";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import TextField from "@mui/material/TextField";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";

import SearchIcon from "@mui/icons-material/Search";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "././components/Nav.css";

function GetVendorFood(props) {
  console.log(props.children.shopName);
  /*
  axios
    .post("http://localhost:5000/food/vendor-food", Vendor)
    .then((response) => {
      console.log(response.data);
    })
    .catch((error) => {
      console.log(error);
    });
    */
}

/*
function ShowVendorFood(props) {
  axios
    .post("http://localhost:5000/food/vendor-food", Vendor)
    .then((response) => {
      console.log(response.data);
    })
    .catch((error) => {
      console.log(error);
    });
}
*/

export default function UsersList() {
  const length = 5;
  const [vendors, setVendors] = useState([]);
  const [array, setArray] = useState(new Array(length).fill(0));

  useEffect(() => {
    axios
      .get("http://localhost:5000/food/vendor-food")
      .then((response) => {
        setVendors(response.data);
      })
      .catch((error) => {});
  }, []);

  function ShowVendorFood(props) {
    return (
      <Paper>
        <Table size="small">
          <TableHead align="center">
            <TableRow>
              <TableCell>Item Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {props.children.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell align="center">{food.itemName}</TableCell>
                <TableCell align="center">{food.price}</TableCell>
                <TableCell align="center">{food.category}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }

  return (
    <div>
      <Grid
        container
        alignItems="flex-start"
        spacing={0}
        direction="column"
        sx={{ borderRadius: 16 }}
      >
        <br></br>
        <Grid item xs={6}>
          <Paper>
            <Table size="small">
              <TableHead>
                <TableRow>
                  <TableCell>Vendor Name</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {vendors.map((vendor, ind) => (
                  <TableRow key={ind}>
                    <TableCell>
                      {vendor._id}
                      <Button
                        size="small"
                        onClick={function () {
                          const list = [];
                          for (var i = 0; i < length; i++) {
                            if (i == ind && array[ind] == 0) list.push(1);
                            else if (i == ind && array[ind] == 1) list.push(0);
                            else list.push(array[i]);
                          }
                          setArray(list);
                        }}
                      >
                        <FastfoodIcon></FastfoodIcon>
                      </Button>
                      {array[ind] ? (
                        <ShowVendorFood>{vendor.details}</ShowVendorFood>
                      ) : null}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
}

function BuyerPage() {
  return (
    <header>
      <div className="Navigator">
        <Box
          sx={{
            width: "100%",
            display: "inline-flex",
            alignItems: "center",
            flexDirection: "row",
          }}
        >
          <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
            <Typography>
              <h3>&emsp;&emsp;</h3>
            </Typography>
          </Link>
        </Box>
        <Box
          sx={{
            flexShrink: 0,
            alignItems: "center",
            justifyContent: "flex-end",
          }}
        >
          <Link to="/login">
            <Tooltip TransitionComponent={Zoom} title="Log In" arrow>
              <Button
                sx={{ alignItems: "center", borderRadius: "16px" }}
                size="small"
                variant="contained"
              >
                <LoginIcon>Login</LoginIcon>
              </Button>
            </Tooltip>
          </Link>
        </Box>
        &emsp;
      </div>
    </header>
  );
}
