    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Welcome to Profile Page</h3>
            </Typography>
          </Box>
          &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Link
              to={{
                pathname: "/buyer/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Home" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <HomeIcon fontSize="large"></HomeIcon>
                </Button>
              </Tooltip>
            </Link>
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button sx={{ alignItems: "center", borderRadius: "16px" }}>
                  <LogoutIcon fontSize="large"></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
        </div>
      </header>
      <br></br>
      <Box>
        <Grid
          container
          container
          align="center"
          justify="center"
          alignItems="center"
          spacing={0}
          direction="row"
          justifyContent={"center"}
          spacing={3}
        >
          <Grid item xs={3}>
            <br></br>
            <Button onClick={function() {
              setShowProfile(true);
            }}>View Profile</Button>
            {showProfile ? <ShowProfile>{profile}</ShowProfile> : null}
          </Grid>
        </Grid>
        <br></br>
      </Box>
    </div>
  );
}
