import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation, useParams } from "react-router-dom";
import Badge from "@mui/material/Badge";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import ShoppingCartCheckoutIcon from "@mui/icons-material/ShoppingCartCheckout";
import { Typography } from "@mui/material";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LogoutIcon from "@mui/icons-material/Logout";
import ShoppingBagIcon from "@mui/icons-material/ShoppingBag";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import { styled, alpha } from "@mui/material/styles";
import Toolbar from "@mui/material/Toolbar";
import InputBase from "@mui/material/InputBase";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import AddCardIcon from "@mui/icons-material/AddCard";
import TextField from "@mui/material/TextField";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "./Nav.css";

export default function VendorPage() {
  // const location = useLocation();
  const _id = "" + useParams().username;
  const [vendorFood, setVendorFood] = useState([]);
  const [shopName, setShopName] = useState("");

  useEffect(() => {
    axios
      .post("http://localhost:5000/vendor/vendor-name", { _id })
      .then((response) => {
        console.log(response.data.shopName);
        setShopName(response.data.shopName);
      })
      .catch((error) => {});
  }, []);

  function ShowVendorFood(props) {
    return (
      <Paper>
          <Table size="small">
            <TableHead>
              <TableRow>
                <TableCell>Item Name</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Category</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {props.children.map((food, ind) => (
                <TableRow key={ind}>
                  <TableCell>{food.itemName}</TableCell>
                  <TableCell>{food.price}</TableCell>
                  <TableCell>{food.category}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
      </Paper>
    );
  }

  const [displayFood, setDisplayFood] = useState(false);

  function FoodList() {
    return (
      <Grid container alignItems="flex-start" spacing={0} direction="column">
        <Button
          onClick={function () {
            if (displayFood == false) {
              setDisplayFood(true);
              axios
                .post("http://localhost:5000/food/food-vendor", { shopName })
                .then((response) => {
                  console.log(response.data);
                  setVendorFood(response.data);
                })
                .catch((error) => {});
            } else setDisplayFood(false);
          }}
        >
          Display Food Items
        </Button>
        {displayFood ? <ShowVendorFood>{vendorFood}</ShowVendorFood> : null}
      </Grid>
    );
  }

  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Vendor food Items</h3>
            </Typography>
          </Box>
          &emsp; &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Tooltip TransitionComponent={Zoom} title="Current Orders" arrow>
              <Button
                sx={{ alignItems: "center", borderRadius: "16px" }}
                size="small"
              >
                <ShoppingBagIcon fontSize="large"></ShoppingBagIcon>
              </Button>
            </Tooltip>
            <Link
              to={{
                pathname: "/vendor/profile/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Profile" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <Badge badgeContent={0} color="error">
                    <AccountCircleIcon fontSize="large"></AccountCircleIcon>
                  </Badge>
                </Button>
              </Tooltip>
            </Link>
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button sx={{ alignItems: "center", borderRadius: "16px" }}>
                  <LogoutIcon fontSize="large"></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
        </div>
      </header>
      <Box>
        <Grid container justifyContent={"center"} direction="row" spacing={2}>
          <Grid item xs={3.5}>
            <br></br>
            <FoodList></FoodList>
          </Grid>
        </Grid>
      </Box>
    </div>
  );
}
