import React, { useState } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@mui/material";
import Button from "@mui/material/Button";
import { makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";
import Box from "@mui/material/Box";
import Fab from "@mui/material/Fab";
import CachedIcon from "@mui/icons-material/Cached";

const createStyles = makeStyles({
  field: {
    marginTop: 40,
    marginBottom: 40,
  },
});

function RegisterVendors() {
  const [managerName, setManagerName] = useState("");
  const [shopName, setShopName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const [password, setPassword] = useState("");

  // opening time of the shop
  const [openHH, setOpenHH] = useState("0");
  const [openMM, setOpenMM] = useState("0");
  const [openTime, setOpenTime] = useState("00 : 00");

  // close time of the shop
  const [closeHH, setCloseHH] = useState("0");
  const [closeMM, setCloseMM] = useState("0");
  const [closeTime, setCloseTime] = useState("00 : 00");

  const resetInputs = () => {
    setManagerName("");
    setShopName("");
    setEmail("");
    setContact("");
    setOpenHH("0");
    setOpenMM("0");
    setOpenTime("00 : 00");
    setCloseHH("0");
    setCloseMM("0");
    setCloseTime("00 : 00");
    setPassword("");
  };

  const [managerNameError, setManagerNameError] = useState(false);
  const [shopNameError, setShopNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [contactError, setContactError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const [openHHError, setOpenHHError] = useState(false);
  const [openMMError, setOpenMMError] = useState(false);

  const [closeHHError, setCloseHHError] = useState(false);
  const [closeMMError, setCloseMMError] = useState(false);

  const useStyles = createStyles();

  const addVendortoDB = () => {
    const Vendor = {
      managerName: managerName,
      shopName: shopName,
      email: email,
      contact: contact,
      openTime: openTime,
      closeTime: closeTime,
      password: password,
    };

    axios
      .post("http://localhost:5000/vendor/register", Vendor)
      .then((response) => {
        alert(
          response.data.shopName +
            " , you have been registered successfully !"
        );
        console.log(response.data);
      })
      .catch(function (err) {
        alert(err.response.data.error);
        <Link to="/login"></Link>;
        console.log(err.response.data);
      });

    resetInputs();
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    if (managerName == "") setManagerNameError(true);
    if (shopName == "") setShopNameError(true);
    if (email == "") setEmailError(true);
    if (contact == "") setContactError(true);
    if (openHH == "" || openHH < 0 || openHH > 23) setOpenHHError(true);
    if (openMM == "" || openMM < 0 || openMM > 59) setOpenMMError(true);
    if (closeHH == "" || closeHH < 0 || closeHH > 23) setCloseHHError(true);
    if (closeMM == "" || closeMM < 0 || closeMM > 59) setCloseMMError(true);
    if (password == "") setPasswordError(true);
    if (
      managerNameError == true ||
      shopNameError == true ||
      emailError == true ||
      contactError == true
    )
      return;
    if (
      openHHError == true ||
      openMMError == true ||
      closeHHError == true ||
      closeMMError == true ||
      passwordError == true
    )
      return;
    else {
      addVendortoDB();
    }
  };

  return (
    <div className="Login">
      <Grid
        container
        align="center"
        justify="center"
        alignItems="center"
        spacing={0}
        direction="column"
        alignItems="center"
        className={useStyles.field}
        sx={{ borderRadius: 16 }}
      >
        <Grid
          item
          xs={8}
          style={({ border: "2px solid" }, { minWidth: "100vh" })}
        >
          <form noValidate autoComplete="off">
            <TextField
              value={managerName}
              size="small"
              onChange={(e) => setManagerName(e.target.value)}
              label="Manager Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={managerNameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={shopName}
              size="small"
              onChange={(e) => setShopName(e.target.value)}
              label="Shop Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={shopNameError}
            />
            <br></br>
            <br></br>
            <TextField
              value={email}
              size="small"
              onChange={(e) => setEmail(e.target.value)}
              label="Email ID"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={emailError}
            />
            <br></br>
            <br></br>
            <TextField
              value={contact}
              size="small"
              onChange={(e) => setContact(e.target.value)}
              label="Contact"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={contactError}
            />
            <Grid
              container
              align="center"
              justify="center"
              alignItems="center"
              spacing={2}
              direction="column"
              alignItems="center"
