function RegisterBuyers() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");

  const [age, setAge] = useState("");
  const minAge = 16;
  const maxAge = 60;

  const [batch, setBatch] = useState("");
  const [password, setPassword] = useState("");

  const resetInputs = () => {
    setName("");
    setEmail("");
    setContact("");
    setAge("");
    setBatch("");
    setPassword("");
  };

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [contactError, setContactError] = useState(false);
  const [ageError, setAgeError] = useState(false);
  const [batchError, setBatchError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const useStyles = createStyles();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (name == "") setNameError(true);
    if (email == "") setEmailError(true);
    if (contact == "") setContactError(true);
    if (age == "" || age < minAge || age > maxAge) setAgeError(true);
    if (batch == "") setBatchError(true);
    if (password == "") setPasswordError(true);
    else {
      const Buyer = {
        name: name,
        email: email,
        contact: contact,
        age: age,
        batch: batch,
        wallet: 0,
        orders: [],
        password: password,
      };

      axios
        .post("http://localhost:5000/buyer/register", Buyer)
        .then((response) => {
          alert(
            response.data.name + " , you have been registered successfully !"
          );
          console.log(response.data);
        })
        .catch((err) => {
          alert("Account Already exists ! Please Login.");
          console.log(err);
        });

      resetInputs();
    }
  };

