import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation, useParams } from "react-router-dom";
import Badge from "@mui/material/Badge";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { Typography } from "@mui/material";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LogoutIcon from "@mui/icons-material/Logout";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import { styled, alpha } from "@mui/material/styles";
import Toolbar from "@mui/material/Toolbar";
import InputBase from "@mui/material/InputBase";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import AddCardIcon from "@mui/icons-material/AddCard";
import TextField from "@mui/material/TextField";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "/home/rutts07/IIITH_Files/DASS/Assignment1/frontend/src/components/Nav.css";

const Search = styled("div")(({ theme }) => ({
  position: "relative",
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: "100%",
  [theme.breakpoints.up("sm")]: {
    marginLeft: theme.spacing(1),
    width: "auto",
  },
}));

const SearchIconWrapper = styled("div")(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: "100%",
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      width: "12ch",
      "&:focus": {
        width: "20ch",
      },
    },
  },
}));

function BasicMenu() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <Button
        id="basic-button"
        variant="outlined"
        sx={{ color: "black" }}
        aria-controls={open ? "basic-menu" : undefined}
        aria-haspopup="true"
        aria-expanded={open ? "true" : undefined}
        onClick={handleClick}
      >
        PROFILE
      </Button>
      <Menu
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          "aria-labelledby": "basic-button",
        }}
      >
        <MenuItem onClick={handleClose}>Profile</MenuItem>
        <MenuItem onClick={handleClose}>Wallet</MenuItem>
      </Menu>
    </div>
  );
}

export default function BuyerPage() {
  // const location = useLocation();
  const _id = "" + useParams().username;

  const [showWallet, setShowWallet] = useState(false);
  const [walletBalance, setWalletBalance] = useState(0);

  const [changeWallet, setChangeWallet] = useState("0");
  const [walletError, setWalletError] = useState(false);

  const [search, setSearch] = useState("");

  const length = 5;
  const [array, setArray] = useState(new Array(length).fill(0));

  useEffect(() => {
    axios
      .post("http://localhost:5000/buyer/wallet_balance", { _id })
      .then((response) => {
        setWalletBalance(response.data);
      })
      .catch((error) => {});
  }, []);

  function WalletCard() {
    return (
      <Box>
        <Card alignItems="flex-start" sx={{ maxWidth: 275 }}>
          <CardContent sx={{ alignItems: "center" }} variant="outlined">
            <Typography
              sx={{ alignItems: "center", fontSize: 20 }}
              color="text.secondary"
              gutterBottom
            >
              Your Current Balance :
            </Typography>
            <Box
              sx={{
                justifyContent: "center",
                alignItems: "center",
                fontSize: 30,
              }}
            >
              {walletBalance}
            </Box>
            <br></br>
            <Box
              sx={{
                alignItems: "center",
              }}
            >
              <Grid container spacing={1}>
                <Grid item>
                  <Button
                    variant="contained"
                    onClick={function () {
                      setWalletBalance(walletBalance + 25);

                      const BuyerDetails = {
                        _id: _id,
                        wallet: walletBalance + 25,
                      };

                      axios
                        .post(
                          "http://localhost:5000/buyer/change-wallet_balance",
                          BuyerDetails
                        )
                        .then((response) => {
                          console.log(response.data);
                        })
                        .catch((error) => {});
                    }}
                  >
                    +25
                  </Button>
                </Grid>
                <Grid item>
                  <Button
                    variant="contained"
                    onClick={function () {
                      setWalletBalance(walletBalance + 50);

                      const BuyerDetails = {
                        _id: _id,
                        wallet: walletBalance + 50,
                      };

                      axios
                        .post(
                          "http://localhost:5000/buyer/change-wallet_balance",
                          BuyerDetails
                        )
                        .then((response) => {
                          console.log(response.data);
                        })
                        .catch((error) => {});
                    }}
                  >
                    +50
                  </Button>
                </Grid>
                <Grid item>
                  <Button
                    variant="contained"
                    onClick={function () {
                      setWalletBalance(walletBalance + 75);

                      const BuyerDetails = {
                        _id: _id,
                        wallet: walletBalance + 75,
                      };

                      axios
                        .post(
                          "http://localhost:5000/buyer/change-wallet_balance",
                          BuyerDetails
                        )
                        .then((response) => {
                          console.log(response.data);
                        })
                        .catch((error) => {});
                    }}
                  >
                    +75
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </CardContent>
        </Card>
        &emsp;
      </Box>
    );
  }

  const [foods, setFoods] = useState([]);
  const [showFood, setShowFood] = useState(false);

  function SearchFood() {
    axios
      .post("http://localhost:5000/food/search-food", { search })
      .then((response) => {
        setFoods(response.data);
        if (response.data.length > 0) {
          setSearch("");
          setShowFood(true);
        } else {
          alert("Item not found !");
          setShowFood(false);
        }
      })
      .catch((error) => {});
  }

  function DisplaySearchedFood() {
    return (
      <Paper>
        <Typography><h3>Search Results :</h3></Typography>
        <Table size="small">
          <TableHead align="center">
            <TableRow>
              <TableCell>Shop Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {foods.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell align="center">{food.shopName}</TableCell>
                <TableCell align="center">{food.price}</TableCell>
                <TableCell align="center">{food.category}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }

  function UsersList() {
    const [vendors, setVendors] = useState([]);

    useEffect(() => {
      axios
        .get("http://localhost:5000/food/vendor-food")
        .then((response) => {
          setVendors(response.data);
        })
        .catch((error) => {});
    }, []);

    function ShowVendorFood(props) {
      return (
        <Paper>
          <Table size="small">
            <TableHead align="center">
              <TableRow>
                <TableCell>Item Name</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Category</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {props.children.map((food, ind) => (
                <TableRow key={ind}>
                  <TableCell align="center">{food.itemName}</TableCell>
                  <TableCell align="center">{food.price}</TableCell>
                  <TableCell align="center">{food.category}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      );
    }

    return (
      <Box marginLeft={"40px"}>
        <Grid
          container
          alignItems="flex-start"
          spacing={1}
          direction="column"
          sx={{ borderRadius: 16 }}
        >
          <Grid item xs={3}>
            <Paper>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Vendor Name</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {vendors.map((vendor, ind) => (
                    <TableRow key={ind}>
                      <TableCell>
                        {vendor._id}
                        <Button
                          size="small"
                          onClick={function () {
                            const list = [];
                            for (var i = 0; i < length; i++) {
                              if (i == ind && array[ind] == 0) list.push(1);
                              else if (i == ind && array[ind] == 1)
                                list.push(0);
                              else list.push(array[i]);
                            }
                            setArray(list);
                          }}
                        >
                          <FastfoodIcon></FastfoodIcon>
                        </Button>
                        {array[ind] ? (
                          <ShowVendorFood>{vendor.details}</ShowVendorFood>
                        ) : null}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    );
  }

  const [searchError, setSearchError] = useState(false);

  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Food Items</h3>
            </Typography>
            &emsp; &emsp; &emsp;
            <TextField
              value={search}
              size="small"
              onChange={(e) => setSearch(e.target.value)}
              label="Search"
              type="text"
              variant="outlined"
              color="primary"
              error={searchError}
            />
            &emsp;
            <Button
              variant="contained"
              color="primary"
              onClick={function () {
                if (search == "") setSearchError(true);
                else SearchFood();
              }}
            >
              <SearchIcon />
            </Button>
          </Box>
          &emsp; &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Tooltip TransitionComponent={Zoom} title="Cart" arrow>
              <Button
                sx={{ alignItems: "center", borderRadius: "16px" }}
                size="small"
              >
                <Badge badgeContent={0} color="error">
                  <ShoppingCartIcon fontSize="large"></ShoppingCartIcon>
                </Badge>
              </Button>
            </Tooltip>

            <Button
              sx={{ alignItems: "center", borderRadius: "16px" }}
              size="small"
              onClick={function () {
                setShowWallet(!showWallet);
              }}
            >
              <MonetizationOnIcon fontSize="large"></MonetizationOnIcon>
            </Button>

            <Link
              to={{
                pathname: "profile/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Profile" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <Badge badgeContent={0} color="error">
                    <AccountCircleIcon fontSize="large"></AccountCircleIcon>
                  </Badge>
                </Button>
              </Tooltip>
            </Link>
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button sx={{ alignItems: "center", borderRadius: "16px" }}>
                  <LogoutIcon fontSize="large"></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
        </div>
      </header>
      <Box>
        <Grid container direction="row" spacing={0}>
          <Grid item xs={4}>
            <br></br>
            <UsersList></UsersList>
          </Grid>
          <Grid item xs={2}>
            <br></br>
              {showFood ? <DisplaySearchedFood></DisplaySearchedFood> : null}
          </Grid>
          <Grid item xs={3.5}></Grid>
          <Grid item xs={2.5}>
            <br></br>
            {showWallet ? <WalletCard></WalletCard> : null} &emsp;
          </Grid>
        </Grid>
        <br></br>
      </Box>
    </div>
  );
}
