import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import { Typography } from "@mui/material";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LogoutIcon from "@mui/icons-material/Logout";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import TextField from "@mui/material/TextField";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";

import SearchIcon from "@mui/icons-material/Search";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "/home/rutts07/IIITH_Files/DASS/Assignment1/frontend/src/components/Nav.css";

function BasicMenu() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <Button
        id="basic-button"
        variant="outlined"
        sx={{ color: "black" }}
        aria-controls={open ? "basic-menu" : undefined}
        aria-haspopup="true"
        aria-expanded={open ? "true" : undefined}
        onClick={handleClick}
      >
        PROFILE
      </Button>
      <Menu
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          "aria-labelledby": "basic-button",
        }}
      >
        <MenuItem onClick={handleClose}>Profile</MenuItem>
        <MenuItem onClick={handleClose}>Wallet</MenuItem>
      </Menu>
    </div>
  );
}

function UsersList() {
  const length = 5;
  const [vendors, setVendors] = useState([]);
  const [array, setArray] = useState(new Array(length).fill(0));

  useEffect(() => {
    axios
      .get("http://localhost:5000/food/vendor-food")
      .then((response) => {
        setVendors(response.data);
      })
      .catch((error) => {});
  }, []);

  function ShowVendorFood(props) {
    return (
      <Paper>
        <Table size="small">
          <TableHead align="center">
            <TableRow>
              <TableCell>Item Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {props.children.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell align="center">{food.itemName}</TableCell>
                <TableCell align="center">{food.price}</TableCell>
                <TableCell align="center">{food.category}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }

  return (
    <div>
      <Box marginLeft={"40px"} marginRight={"80px"}>
        <Box></Box>
        <Grid
          container
          alignItems="flex-start"
          spacing={0}
          direction="column"
          sx={{ borderRadius: 16 }}
        >
          <br></br>
          <Grid item xs={6}>
            <Paper>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Vendor Name</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {vendors.map((vendor, ind) => (
                    <TableRow key={ind}>
                      <TableCell>
                        {vendor._id}
                        <Button
                          size="small"
                          onClick={function () {
                            const list = [];
                            for (var i = 0; i < length; i++) {
                              if (i == ind && array[ind] == 0) list.push(1);
                              else if (i == ind && array[ind] == 1)
                                list.push(0);
                              else list.push(array[i]);
                            }
                            setArray(list);
                          }}
                        >
                          <FastfoodIcon></FastfoodIcon>
                        </Button>
                        {array[ind] ? (
                          <ShowVendorFood>{vendor.details}</ShowVendorFood>
                        ) : null}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </div>
  );
}

export default function BuyerPage() {

  function WalletCard() {
    return (
      <Card alignItems="flex-start" sx={{ maxWidth: 275 }}>
        <CardContent alignItems="center" variant="outlined">
          <Typography sx={{ fontSize: 20 }} color="text.secondary" gutterBottom>
            Your Current Balance :
          </Typography>
          <Typography sx={{ mb: 1.5 }} color="text.secondary">
            adjective
          </Typography>
          <Typography variant="body2">
            well meaning and kindly.
            <br />
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Food Items</h3>
            </Typography>
          </Box>
          &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Tooltip TransitionComponent={Zoom} title="Wallet" arrow>
              <Button
                sx={{ alignItems: "center", borderRadius: "16px" }}
                size="small"
                onClick={function () {
                  const {message} = useLocation();
                  console.log(message);
                }}
              >
                <MonetizationOnIcon fontSize="large"></MonetizationOnIcon>
              </Button>
            </Tooltip>
            <Link to="/profile">
