import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation, useParams } from "react-router-dom";
import Badge from "@mui/material/Badge";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import ShoppingCartCheckoutIcon from "@mui/icons-material/ShoppingCartCheckout";
import { Typography } from "@mui/material";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LogoutIcon from "@mui/icons-material/Logout";
import ShoppingBagIcon from "@mui/icons-material/ShoppingBag";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import { styled, alpha } from "@mui/material/styles";
import Toolbar from "@mui/material/Toolbar";
import InputBase from "@mui/material/InputBase";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import AddCardIcon from "@mui/icons-material/AddCard";
import TextField from "@mui/material/TextField";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "./Nav.css";

export default function VendorPage() {
  // const location = useLocation();
  const _id = "" + useParams().username;
  const [vendorFood, setVendorFood] = useState([]);
  const [shopName, setShopName] = useState("");

  const [editMenu, setEditMenu] = useState(false);

  useEffect(() => {
    axios
      .post("http://localhost:5000/vendor/vendor-name", { _id })
      .then((response) => {
        setShopName(response.data.shopName);
      })
      .catch((error) => {});
  }, []);

  function AddFoodItems() {
    const [itemName, setItemName] = useState("");
    const [itemError, setItemError] = useState(false);

    const [price, setPrice] = useState(0);
    const [priceError, setPriceError] = useState(false);

    const [category, setCategory] = useState("");
    const [categoryError, setCategoryError] = useState(false);

    function ResetInputs() {
      setItemName("");
      setPrice("");
      setCategory("");
    }

    return (
      <div>
        <br></br>
        <form noValidate autoComplete="off">
          <Paper sx={{ alignItems: "center" }}>
            <Typography>Add Food Items</Typography>
            <br></br>
            <TextField
              value={itemName}
              size="small"
              onChange={(e) => setItemName(e.target.value)}
              label="Item Name"
              type="text"
              variant="outlined"
              color="primary"
              required
              error={itemError}
            />
            <br></br>
            <br></br>
            <TextField
              value={price}
              size="small"
              onChange={(e) => setPrice(e.target.value)}
              InputProps={{ inputProps: { min: 0 } }}
              label="Price"
              type="Number"
              variant="outlined"
              color="primary"
              required
              error={priceError}
            />
            <br></br>
            <br></br>
            <Box
              sx={{
                "& > :not(style)": { m: 1 },
                alignItems: "center",
                justifyContent: "center",
                borderRadius: "12px",
              }}
            >
              <TextField
                value={category}
                disabled
                size="small"
                onChange={(e) => setCategory(e.target.value)}
                label="Select Category"
                variant="outlined"
                color="primary"
                required
                error={categoryError}
              />
              <br></br>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setCategory("Veg");
                }}
              >
                VEG
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                size="small"
                onClick={function () {
                  setCategory("Non-Veg");
                }}
              >
                NON-VEG
              </Button>
              <br></br>
              <Button
                variant="contained"
                color="success"
                onClick={function () {
                  if (itemName == "") setItemError(true);
                  if (isNaN(price) || price < 0) setPriceError(true);
                  if (category == "") setCategoryError(true);
                  else {
                    const FoodItem = {
                      shopName: shopName,
                      itemName: itemName,
                      price: price,
                      category: category,
                    };

                    axios
                      .post(
                        "http://localhost:5000/food/vendor-food/add",
                        FoodItem
                      )
                      .then((response) => {
                        ResetInputs();
                        axios
                          .post("http://localhost:5000/food/food-vendor", {
                            shopName,
                          })
                          .then((response) => {
                            setVendorFood(response.data);
                          })
                          .catch((error) => {});

                        if (displayFood == true) {
                          setDisplayFood(false);
                          setTimeout(function () {
                            setDisplayFood(true);
                          }, 3000);
                        }
                      })
                      .catch((error) => {
                        console.log(error);
                      });
                  }
                }}
              >
                Add
              </Button>
              <br></br>
            </Box>
          </Paper>
          <br></br>
        </form>
      </div>
    );
  }

  function ShowVendorFood(props) {
    return (
      <Paper>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Item Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {props.children.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell>{food.itemName}</TableCell>
                <TableCell>{food.price}</TableCell>
                <TableCell>
                  {food.category}
                  <Button
                    onClick={function () {
                      const FoodDets = {
                        shopName: shopName,
                        itemName: food.itemName,
                      };

                      axios
                        .post(
                          "http://localhost:5000/food/vendor-food/delete",
                          FoodDets
                        )
                        .then((response) => {
                          axios
                            .post("http://localhost:5000/food/food-vendor", {
                              shopName,
                            })
                            .then((response) => {
                              setVendorFood(response.data);
                            })
                            .catch((error) => {});

                          if (displayFood == true) {
                            setDisplayFood(false);
                            setTimeout(function () {
                              setDisplayFood(true);
                            }, 3000);
                          }
                        })
                        .catch((error) => {
                          alert("Item does not exist !");
                        });
                    }}
                  >
                    <DeleteForeverIcon></DeleteForeverIcon>
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }

  const [displayFood, setDisplayFood] = useState(false);

  function FoodList() {
    return (
      <Grid container alignItems="center" spacing={0} direction="column">
        <Button
          variant="outlined"
          onClick={function () {
            if (displayFood == false) {
              setDisplayFood(true);
              axios
                .post("http://localhost:5000/food/food-vendor", { shopName })
                .then((response) => {
                  setVendorFood(response.data);
                })
                .catch((error) => {});
            } else setDisplayFood(false);
          }}
        >
          Display Food Items
        </Button>
        <br></br>
        {displayFood ? <ShowVendorFood>{vendorFood}</ShowVendorFood> : null}
      </Grid>
    );
  }

  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Vendor food Items&emsp;{shopName}</h3>
            </Typography>
          </Box>
          &emsp; &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Link
              to={{
                pathname: "/vendor/orders/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Current Orders" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <ShoppingBagIcon fontSize="large"></ShoppingBagIcon>
                </Button>
              </Tooltip>
            </Link>

            <Link
              to={{
                pathname: "/vendor/profile/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Profile" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <Badge badgeContent={0} color="error">
                    <AccountCircleIcon fontSize="large"></AccountCircleIcon>
                  </Badge>
                </Button>
              </Tooltip>
            </Link>
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button sx={{ alignItems: "center", borderRadius: "16px" }}>
                  <LogoutIcon fontSize="large"></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
        </div>
      </header>
      <Box>
        <Grid
          container
          justify="center"
          align="center"
          justifyContent={"center"}
          direction="row"
          spacing={2}
        >
          <Grid item xs={5}>
            <br></br>
            <FoodList></FoodList>
          </Grid>
          <Grid item xs={5}>
            <br></br>
            <Button
              variant="outlined"
              onClick={function () {
                if (editMenu == false) {
                  setEditMenu(true);
                } else setEditMenu(false);
              }}
            >
              Add a food item
            </Button>
            <br></br>
            {editMenu ? <AddFoodItems></AddFoodItems> : null}
          </Grid>
        </Grid>
      </Box>
    </div>
  );
}
