import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation, useParams } from "react-router-dom";
import Badge from "@mui/material/Badge";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import ShoppingCartCheckoutIcon from "@mui/icons-material/ShoppingCartCheckout";
import { Typography } from "@mui/material";
import Box from "@mui/material/Box";
import Tooltip from "@mui/material/Tooltip";
import Zoom from "@mui/material/Zoom";
import LogoutIcon from "@mui/icons-material/Logout";
import ShoppingBagIcon from "@mui/icons-material/ShoppingBag";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import { styled, alpha } from "@mui/material/styles";
import Toolbar from "@mui/material/Toolbar";
import InputBase from "@mui/material/InputBase";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import AddCardIcon from "@mui/icons-material/AddCard";
import TextField from "@mui/material/TextField";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import "./Nav.css";

export default function BuyerPage() {
  // const location = useLocation();
  const _id = "" + useParams().username;

  const [showWallet, setShowWallet] = useState(false);
  const [walletBalance, setWalletBalance] = useState(0);

  const [changeWallet, setChangeWallet] = useState("0");
  const [walletError, setWalletError] = useState(false);

  const [search, setSearch] = useState("");

  // Adjust the items of each vendor
  const length = 5;
  const [array, setArray] = useState(new Array(length).fill(0));

  useEffect(() => {
    axios
      .post("http://localhost:5000/buyer/wallet_balance", { _id })
      .then((response) => {
        setWalletBalance(response.data);
      })
      .catch((error) => {});

    axios
      .post("http://localhost:5000/buyer/orders", { _id })
      .then((response) => {
        setItemsOnCart(response.data.length);
        setCartItems(response.data);
      })
      .catch((error) => {});
  }, []);

  function WalletCard() {
    return (
      <Box>
        <Card alignItems="flex-start" sx={{ maxWidth: 275 }}>
          <CardContent sx={{ alignItems: "center" }} variant="outlined">
            <Typography
              sx={{ alignItems: "center", fontSize: 20 }}
              color="text.secondary"
              gutterBottom
            >
              Your Current Balance :
            </Typography>
            <Box
              sx={{
                justifyContent: "center",
                alignItems: "center",
                fontSize: 30,
              }}
            >
              {walletBalance}
            </Box>
            <br></br>
            <Box
              sx={{
                alignItems: "center",
              }}
            >
              <Grid container spacing={1}>
                <Grid item>
                  <Button
                    variant="contained"
                    onClick={function () {
                      setWalletBalance(walletBalance + 25);

                      const BuyerDetails = {
                        _id: _id,
                        wallet: walletBalance + 25,
                      };

                      axios
                        .post(
                          "http://localhost:5000/buyer/change-wallet_balance",
                          BuyerDetails
                        )
                        .then((response) => {
                          console.log(response.data);
                        })
                        .catch((error) => {});
                    }}
                  >
                    +25
                  </Button>
                </Grid>
                <Grid item>
                  <Button
                    variant="contained"
                    onClick={function () {
                      setWalletBalance(walletBalance + 50);

                      const BuyerDetails = {
                        _id: _id,
                        wallet: walletBalance + 50,
                      };

                      axios
                        .post(
                          "http://localhost:5000/buyer/change-wallet_balance",
                          BuyerDetails
                        )
                        .then((response) => {
                          console.log(response.data);
                        })
                        .catch((error) => {});
                    }}
                  >
                    +50
                  </Button>
                </Grid>
                <Grid item>
                  <Button
                    variant="contained"
                    onClick={function () {
                      setWalletBalance(walletBalance + 75);

                      const BuyerDetails = {
                        _id: _id,
                        wallet: walletBalance + 75,
                      };

                      axios
                        .post(
                          "http://localhost:5000/buyer/change-wallet_balance",
                          BuyerDetails
                        )
                        .then((response) => {
                          console.log(response.data);
                        })
                        .catch((error) => {});
                    }}
                  >
                    +75
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </CardContent>
        </Card>
        &emsp;
      </Box>
    );
  }

  const [foods, setFoods] = useState([]);
  const [showFood, setShowFood] = useState(false);

  function SearchFood() {
    axios
      .post("http://localhost:5000/food/search-food", { search })
      .then((response) => {
        setFoods(response.data);
        if (response.data.length > 0) {
          setSearch("");
          setShowFood(true);
        } else {
          alert("Item not found !");
          setShowFood(false);
        }
      })
      .catch((error) => {});
  }

  function DisplaySearchedFood() {
    return (
      <Paper>
        <Typography
          sx={{ fontSize: 18 }}
          color="text.secondary"
          marginLeft={"15px"}
        >
          Search Results
        </Typography>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Shop</TableCell>
              <TableCell>Price</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {foods.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell>{food.shopName}</TableCell>
                <TableCell>{food.price}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }

  const [itemsOnCart, setItemsOnCart] = useState(0);
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [cartTotal, setCartTotal] = useState(0);

  function CartItems(props) {
    setCartTotal(props.children.reduce((a, v) => (a = a + v.price), 0));
    return (
      <Paper sx={{ alignItems: "center" }}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Shop</TableCell>
              <TableCell>Item Name</TableCell>
              <TableCell>Price</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {props.children.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell>{food.shopName}</TableCell>
                <TableCell>{food.itemName}</TableCell>
                <TableCell>
                  {food.price}
                  <Button
                    onClick={function () {
                      setItemsOnCart(itemsOnCart - 1);
                      const s_id = food._id;

                      axios
                        .post("http://localhost:5000/buyer/delete-food", {
                          _id,
                          s_id,
                        })
                        .then(() => {})
                        .catch((error) => {
                          console.log(error);
                        });

                      axios
                        .post("http://localhost:5000/buyer/orders", { _id })
                        .then((response) => {
                          setCartItems(response.data);
                        })
                        .catch((error) => {});

                      if (showCart == true) {
                        setShowCart(false);
                        setTimeout(function () {
                          setShowCart(true);
                        }, 3000);
                      }
                    }}
                  >
                    <DeleteForeverIcon></DeleteForeverIcon>
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <Box sx={{ alignItems: "center" }} marginLeft={"140px"}>
          <Typography
            sx={{ alignItems: "center", fontSize: 20 }}
            color="text.secondary"
            gutterBottom
          >
            Total : {cartTotal}{" "}
            <Button>
              <ShoppingCartCheckoutIcon
                onClick={function () {
                  if (cartTotal > walletBalance)
                    alert(
                      "Insufficient Balance ! Delete Items or Add Money !!!"
                    );
                  else setWalletBalance(walletBalance - cartTotal);
                }}
              ></ShoppingCartCheckoutIcon>
            </Button>
          </Typography>
        </Box>
      </Paper>
    );
  }

  function UsersList() {
    const [vendors, setVendors] = useState([]);

    useEffect(() => {
      axios
        .get("http://localhost:5000/food/vendor-food")
        .then((response) => {
          setVendors(response.data);
        })
        .catch((error) => {});
    }, []);

    function ShowVendorFood(props) {
      return (
        <Paper>
          <Table size="small">
            <TableHead>
              <TableRow>
                <TableCell>Item Name</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Category</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {props.children.map((food, ind) => (
                <TableRow key={ind}>
                  <TableCell>{food.itemName}</TableCell>
                  <TableCell>{food.price}</TableCell>
                  <TableCell>
                    {food.category}
                    <Button
                      onClick={function () {
                        setItemsOnCart(itemsOnCart + 1);
                        const foodDets = {
                          shopName: props.shopName,
                          itemName: food.itemName,
                          price: food.price,
                        };

                        axios
                          .post("http://localhost:5000/buyer/add-food", {
                            _id,
                            foodDets,
                          })
                          .then(() => {})
                          .catch((error) => {
                            console.log(error);
                          });

                        axios
                          .post("http://localhost:5000/buyer/orders", { _id })
                          .then((response) => {
                            setCartItems(response.data);
                            console.log(cartItems);
                          })
                          .catch((error) => {});

                        if (showCart == true) {
                          setShowCart(false);
                          setTimeout(function () {
                            setShowCart(true);
                          }, 5000);
                        }
                      }}
                    >
                      <AddShoppingCartIcon></AddShoppingCartIcon>
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      );
    }

    return (
      <Grid container alignItems="flex-start" spacing={0} direction="column">
        <Grid item>
          <Paper>
            <Table size="small">
              <TableHead>
                <TableRow>
                  <TableCell>Vendor Name</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {vendors.map((vendor, ind) => (
                  <TableRow key={ind}>
                    <TableCell>
                      {vendor._id}
                      <Button
                        size="small"
                        onClick={function () {
                          const list = [];
                          for (var i = 0; i < length; i++) {
                            if (i == ind && array[ind] == 0) list.push(1);
                            else if (i == ind && array[ind] == 1) list.push(0);
                            else list.push(array[i]);
                          }
                          setArray(list);
                        }}
                      >
                        <FastfoodIcon></FastfoodIcon>
                      </Button>
                      {array[ind] ? (
                        <ShowVendorFood shopName={vendor._id}>
                          {vendor.details}
                        </ShowVendorFood>
                      ) : null}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </Grid>
      </Grid>
    );
  }

  const [searchError, setSearchError] = useState(false);

  return (
    <div>
      <header>
        <div className="Navigator1">
          <Box
            sx={{
              width: "100%",
              display: "inline-flex",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Typography>
              <h3>&emsp;&emsp;Food Items</h3>
            </Typography>
            &emsp; &emsp; &emsp;
            <TextField
              value={search}
              size="small"
              onChange={(e) => setSearch(e.target.value)}
              label="Search"
              type="text"
              variant="outlined"
              color="primary"
              error={searchError}
            />
            &emsp;
            <Button
              variant="contained"
              color="primary"
              onClick={function () {
                if (search == "") setSearchError(true);
                else SearchFood();
              }}
            >
              <SearchIcon />
            </Button>
          </Box>
          &emsp; &emsp;
          <Box
            sx={{
              flexShrink: 0,
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Tooltip TransitionComponent={Zoom} title="Shopping Bag" arrow>
              <Button
                sx={{ alignItems: "center", borderRadius: "16px" }}
                size="small"
              >
                <ShoppingBagIcon fontSize="large"></ShoppingBagIcon>
              </Button>
            </Tooltip>

            <Tooltip TransitionComponent={Zoom} title="Cart" arrow>
              <Button
                sx={{ alignItems: "center", borderRadius: "16px" }}
                size="small"
                onClick={function () {
                  if (showCart == false) setShowCart(true);
                  else setShowCart(false);

                  axios
                    .post("http://localhost:5000/buyer/orders", { _id })
                    .then((response) => {
                      setCartItems(response.data);
                    })
                    .catch((error) => {});
                }}
              >
                <Badge badgeContent={itemsOnCart} color="error">
                  <ShoppingCartIcon fontSize="large"></ShoppingCartIcon>
                </Badge>
              </Button>
            </Tooltip>

            <Button
              sx={{ alignItems: "center", borderRadius: "16px" }}
              size="small"
              onClick={function () {
                setShowWallet(!showWallet);
              }}
            >
              <MonetizationOnIcon fontSize="large"></MonetizationOnIcon>
            </Button>

            <Link
              to={{
                pathname: "http://localhost:3000/profile/" + _id,
              }}
            >
              <Tooltip TransitionComponent={Zoom} title="Profile" arrow>
                <Button
                  sx={{ alignItems: "center", borderRadius: "16px" }}
                  size="small"
                >
                  <Badge badgeContent={0} color="error">
                    <AccountCircleIcon fontSize="large"></AccountCircleIcon>
                  </Badge>
                </Button>
              </Tooltip>
            </Link>
            <Link to="/" style={{ color: "black", textDecoration: "inherit" }}>
              <Tooltip TransitionComponent={Zoom} title="Log Out" arrow>
                <Button sx={{ alignItems: "center", borderRadius: "16px" }}>
                  <LogoutIcon fontSize="large"></LogoutIcon>
                </Button>
              </Tooltip>
            </Link>
          </Box>
        </div>
      </header>
      <Box>
        <Grid container justifyContent={"center"} direction="row" spacing={2}>
          <Grid item xs={3.5}>
            <br></br>
            <UsersList></UsersList>
          </Grid>
          <Grid item xs={1.5}>
            <br></br>
            {showFood ? <DisplaySearchedFood></DisplaySearchedFood> : null}
          </Grid>
          <Grid item xs={3.5}>
            <br></br>
            {showCart ? <CartItems>{cartItems}</CartItems> : null}
          </Grid>
          <Grid item xs={2.5}>
            <br></br>
            {showWallet ? <WalletCard></WalletCard> : null} &emsp;
          </Grid>
        </Grid>
        <br></br>
      </Box>
    </div>
  );
}
