import { useState, useEffect } from "react";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import Button from "@mui/material/Button";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import TextField from "@mui/material/TextField";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import Autocomplete from "@mui/material/Autocomplete";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";

import SearchIcon from "@mui/icons-material/Search";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

function GetVendorFood(props) {
  console.log(props.children.shopName);
  /*
  axios
    .post("http://localhost:5000/food/vendor-food", Vendor)
    .then((response) => {
      console.log(response.data);
    })
    .catch((error) => {
      console.log(error);
    });
    */
}

/*
function ShowVendorFood(props) {
  axios
    .post("http://localhost:5000/food/vendor-food", Vendor)
    .then((response) => {
      console.log(response.data);
    })
    .catch((error) => {
      console.log(error);
    });
}
*/

function range(size, startAt = 0) {
  return [...Array(size).keys()].map((i) => i + startAt);
}

export default function UsersList() {
  const [vendors, setVendors] = useState([]);
  const [buttonState, setButtonState] = useState(false);
  const [buttonState1, setButtonState1] = useState(false);
  let vendorLength = 0;

  useEffect(() => {
    axios
      .get("http://localhost:5000/food/vendor-food")
      .then((response) => {
        setVendors(response.data);
        console.log(vendors);
        vendorLength = vendors.length;
        console.log(vendorLength);
        let i = 0;
        for (i in range(vendorLength)) {
          buttonState1[i] = false;
        }
      })
      .catch((error) => {});
  }, []);

  function ShowVendorFood(props) {
    return (
      <Paper>
        <Table size="small">
          <TableHead align="center">
            <TableRow>
              <TableCell>Item Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {props.children.map((food, ind) => (
              <TableRow key={ind}>
                <TableCell align="center">{food.itemName}</TableCell>
                <TableCell align="center">{food.price}</TableCell>
                <TableCell align="center">{food.category}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }

  return (
    <Grid
      container
      alignItems="center"
      spacing={0}
      direction="column"
      sx={{ borderRadius: 16 }}
    >
      <br></br>
      <Grid item xs={6}>
        <Paper>
          <Table size="small">
            <TableHead align="center">
              <TableRow>
                <TableCell>Vendor Name</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {vendors.map((vendor, ind) => (
                <TableRow key={ind}>
                  <TableCell align="center">
                    {vendor._id}
                    <Button
                      size="small"
                      onClick={function () {
                        if (buttonState == false) setButtonState(true);
                        else setButtonState(false);
                        console.log(buttonState);
                      }}
                    >
                      <FastfoodIcon></FastfoodIcon>
                    </Button>
                    {buttonState ? (
                      <ShowVendorFood>{vendor.details}</ShowVendorFood>
                    ) : null}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      </Grid>
    </Grid>
  );
}
